/**
 * Helper function to ensure images are loaded correctly
 * @param imagePath - Path to the image
 * @returns The full URL for the image
 */
export function getImageUrl(imagePath: string | any): string {
  // Log for debugging
  console.log("Image path type:", typeof imagePath);

  // For module imports (like from ES imports)
  if (typeof imagePath === "string") {
    return imagePath;
  }

  // For imported images that might be objects with default property
  if (imagePath && typeof imagePath === "object" && "default" in imagePath) {
    return imagePath.default;
  }

  // Try to extract URL from object
  if (imagePath && typeof imagePath.toString === "function") {
    try {
      return String(imagePath);
    } catch (e) {
      console.error("Failed to convert image to string:", e);
    }
  }

  console.error("Could not process image path:", imagePath);
  // Return a placeholder image as fallback
  return "data:image/png;base64,";
}
